CREATE TABLE LIVRO (ID BIGINT NOT NULL, DESCRICAO VARCHAR(255), EDITORA VARCHAR(255), ILUSTRACOES SMALLINT DEFAULT 0, ISBN VARCHAR(255), NRODEPAGINAS INTEGER, PRECO FLOAT, TITULO VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE PESSOA (ID BIGINT NOT NULL, CPF VARCHAR(255), PRIMEIRONOME VARCHAR(255), ULTIMONOME VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE t_endereco (ID BIGINT NOT NULL, RUA1 VARCHAR(255), RUA2 VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE t_cidade (ID BIGINT NOT NULL, CEP VARCHAR(255), CIDADE VARCHAR(255), ESTADO VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE t_pais (ID BIGINT NOT NULL, PAIS VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE noticias (CONTEUDO VARCHAR(255), TITULO VARCHAR(255) NOT NULL, IDIOMA VARCHAR(255) NOT NULL, PRIMARY KEY (TITULO, IDIOMA))
CREATE TABLE T_Trilha_Musica (ID BIGINT NOT NULL, DESCRICAO VARCHAR(255), DURACAO FLOAT, MUSICA BLOB(2147483647), TITULO VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE T_Livros (ID BIGINT NOT NULL, DESCRICAO VARCHAR(2000), ILUSTRACOES SMALLINT DEFAULT 0, ISBN VARCHAR(255), nro_de_paginas INTEGER NOT NULL, PRECO FLOAT, livro_titulo VARCHAR(255) NOT NULL, PRIMARY KEY (ID))
CREATE TABLE cartao_credito (NUMERO VARCHAR(255) NOT NULL, BANDEIRACARTAOCREDITO VARCHAR(255), DATAVALIDADE VARCHAR(255), NUMEROCONTROLE INTEGER, PRIMARY KEY (NUMERO))
CREATE TABLE CONSUMIDOR (ID BIGINT NOT NULL, DATACRIACAO TIMESTAMP, DATANASCIMENTO DATE, EMAIL VARCHAR(255), NUMEROTELEFONE VARCHAR(255), PRIMEIRONOME VARCHAR(255), ULTIMONOME VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE CONSUMIDORCAMPO (ID BIGINT NOT NULL, EMAIL VARCHAR(255), numero_telefone VARCHAR(15), primeiro_nome VARCHAR(50) NOT NULL, ultimo_nome VARCHAR(50) NOT NULL, PRIMARY KEY (ID))
CREATE TABLE CONSUMIDOREMB (ID BIGINT NOT NULL, EMAIL VARCHAR(255), numero_telefone VARCHAR(15), primeiro_nome VARCHAR(50) NOT NULL, ultimo_nome VARCHAR(50) NOT NULL, CEP VARCHAR(255), CIDADE VARCHAR(255), ENDERECO1 VARCHAR(255) NOT NULL, ENDERECO2 VARCHAR(255), ESTADO VARCHAR(255), PAIS VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE CONSUMIDORMETODO (ID BIGINT NOT NULL, EMAIL VARCHAR(255), numero_telefone VARCHAR(15), primeiro_nome VARCHAR(50) NOT NULL, ultimo_nome VARCHAR(50) NOT NULL, PRIMARY KEY (ID))
CREATE TABLE CONSUMIDORMISTURADO (ID BIGINT NOT NULL, EMAIL VARCHAR(255), numero_telefone VARCHAR(15), primeiro_nome VARCHAR(50) NOT NULL, ultimo_nome VARCHAR(50) NOT NULL, PRIMARY KEY (ID))
CREATE TABLE LIVROCOLECAO (ID BIGINT NOT NULL, DESCRICAO VARCHAR(255), ILUSTRACOES SMALLINT DEFAULT 0, ISBN VARCHAR(255), NRODEPAGINAS INTEGER, PRECO FLOAT, TITULO VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE Etiqueta (LivroColecao_ID BIGINT, Valor VARCHAR(255))
ALTER TABLE t_cidade ADD CONSTRAINT FK_t_cidade_ID FOREIGN KEY (ID) REFERENCES t_endereco (ID)
ALTER TABLE t_pais ADD CONSTRAINT FK_t_pais_ID FOREIGN KEY (ID) REFERENCES t_endereco (ID)
ALTER TABLE Etiqueta ADD CONSTRAINT tqetaLvroColecaoID FOREIGN KEY (LivroColecao_ID) REFERENCES LIVROCOLECAO (ID)
CREATE TABLE SEQUENCE (SEQ_NAME VARCHAR(50) NOT NULL, SEQ_COUNT DECIMAL(15), PRIMARY KEY (SEQ_NAME))
